import React from 'react';
import {
    GoogleMap,
    Marker,
    useLoadScript,
    // Marker,
    // InfoWindow,
} from "@react-google-maps/api";
import { formatRelative } from "date-fns";


// import {
//     Combobox,
//     ComboboxInput,
//     ComboboxPopover,
//     ComboboxList,
//     ComboboxOption,
//     ComboboxOptionText,
//   } from "@reach/combobox";
import "@reach/combobox/styles.css";

import mapStyles from "./mapStyles.js";

const libraries = ["places"];

const mapContainerStyle = {
    height: "100vh",
    width: "100vw",
};

const options = {
    styles: mapStyles,
    disableDefaultUI: true,
    zoomControl: true,
}
const center = {
    lat: 41.179192,
    lng: -73.189484
};

const APIKEY = "AIzaSyCPJi6Y01w_JspvfKuX55S_a0PATO5ssU4";

export default function App() {
    const { isLoaded, loadError } = useLoadScript({
        googleMapsApiKey: APIKEY,
        // googleMapsApiKey: process.env.REACT_APP_GOOGLE_API_KEY,
        libraries,
    });
    const [markers, setMarkers] = React.useState([]);

    const [selected, setSelected] = React.useState(null);

    const onMapClick = React.useCallback((event) => {
        setMarkers((current) => [
            ...current,
            {
                lat: event.latLng.lat(),
                lng: event.latLng.lng(),
                time: new Date(),
            },
        ]);
    }, [])

    const mapRef = React.useRef();
    const onMapLoad = React.useCallback((map) => {
        mapRef.current = map;
    }, []);



    if (loadError) return "Error loading maps";
    if (!isLoaded) return "Loading Maps";

    return <div>
        <h1>
            Testa De Nevill's Bridge {''}<span role="img" aria-label="crown">🗺️</span></h1>
        <GoogleMap
            mapContainerStyle={mapContainerStyle}
            center={center}
            zoom={8}
            options={options}
            onClick={onMapClick}
            onLoad={onMapLoad}
        >
            {markers.map((marker) => (
                <Marker
                    key={marker.time.toISOString()}
                    position={{ lat: marker.lat, lng: marker.lng }}
                    icon={{
                        url: '/greenFlag.png',
                        scaledSize: new window.google.maps.Size(30, 30),
                        origin: new window.google.maps.Point(0, 0),
                        anchor: new window.google.maps.Point(15, 15),
                    }}
                    onClick={() => {
                        setSelected(marker);
                        console.log(marker.lat, marker.lng)
                    }}
                />
            ))}
            {selected ? (
                <infowWindow position={{ lat: selected.lat, lng: selected.lng }}>
                    <div>
                        <h2>
                            New Location!
                        </h2>
                        <p> Here! {formatRelative(selected.time, new Date())}</p>
                    </div>
                </infowWindow>
            ) : null}
        </GoogleMap>
    </div>;
}
